<template>
  <div class="services container">
    <div class="services-left col-xs-12">
      <p class="services-header">Our Story & Services</p>
      <p class="services-title">Our Culinary Journey And Services</p>
      <p class="services-content">
        Rooted in passion, we curate unforgettable dining experiences and offer
        exceptional services, blending culinary artistry with warm hospitality.
      </p>
      <button class="explore">Explore</button>
    </div>
    <div class="services-right row">
      <card-services v-for="card in cards" :key="card.id" :card="card" />
    </div>
  </div>
</template>

